package com.iotbilisim.listviewornek;//Entity Modelpublic class Kisi {    private String  isim;    private boolean kadinMi;    public Kisi(String isim, boolean kadinMi) {        super();        this.setIsim(isim);        this.setKadinMi(kadinMi);    }    @Override    public String toString() {        return getIsim();    }    public String getIsim() {        return isim;    }    public void setIsim(String isim) {        this.isim = isim;    }    public boolean isKadinMi() {        return kadinMi;    }    public void setKadinMi(boolean kadinMi) {        this.kadinMi = kadinMi;    }}